YUI.add("accelerometer",function(e,t){var n=e.namespace("Accelerometer");n.ERROR={OK:0,SERVICE_UNAVAILABLE:1,FAILED_TO_FETCH_DATA:2},n._serviceId="sAccelerometer",n.getAcceleration=function(t,r){return e.Bridge.invokeMethod(n._serviceId,"getAcceleration",null,{success:t,failure:r})},n.watchAcceleration=function(t,r){return e.Bridge.invokeMethod(n._serviceId,"watchAcceleration",null,{success:t,failure:r})},n.clearWatch=function(t,n,r){e.Bridge.cancelMethod(t,n,r)}},"@VERSION@",{requires:["bridge"]});
